<script lang="ts">
import { defineComponent, onMounted } from 'vue';
import {
    EllipsisOutlined,
    MergeCellsOutlined,
} from '@ant-design/icons-vue';
import {
    CheckTrialCount,
} from '../../../wailsjs/go/main/App';
import { useMenuState } from '../../store/menu';
import { useInsertState } from '../../store/insert';
import { useCutState } from '../../store/cut';
import { useWatermarkState } from '../../store/watermark';
import { useBookmarkState } from '../../store/bookmark';
import { useExtractState } from '../../store/extract';
import { useConvertState } from '../../store/convert';
import { useEncryptState } from '../../store/encrypt';
import { useSignState } from '../../store/sign';
import { useAnnotState } from '../../store/annot';
import { useIndexState } from '../../store/index';
import { message, Modal } from 'ant-design-vue';

export default defineComponent({
    components: {
        EllipsisOutlined,
        MergeCellsOutlined
    },
    setup() {
        const indexStore = useIndexState();
        const menuStore = useMenuState();
        const insertStore = useInsertState();
        const cutStore = useCutState();
        const watermarkStore = useWatermarkState();
        const bookmarkStore = useBookmarkState();
        const extractStore = useExtractState();
        const convertStore = useConvertState();
        const encryptStore = useEncryptState();
        const signStore = useSignState();
        const annotStore = useAnnotState();

        // 软件试用
        onMounted(async () => {
            if (!indexStore.trial_flag) return;
            if (indexStore.trial_set) return;
            await CheckTrialCount().then((res: any) => {
                console.log(res);
                if (res <= indexStore.max_try_times) {
                    indexStore.trial_set = true;
                    Modal.info({
                        title: '温馨提示',
                        content: `软件试用次数共${indexStore.max_try_times}次，当前是第${res}次，请不要轻易关闭软件！`,
                        maskClosable: false,
                    });
                } else {
                    Modal.error({
                        title: '错误',
                        content: '软件试用次数已用完，请购买后使用！',
                        maskClosable: false,
                        okButtonProps: {
                            disabled: true,
                        }
                    });
                }
            }).catch((err: any) => {
                console.log(err);
                Modal.error({
                    title: '错误',
                    content: '软件试用失败，请联系管理员',
                    maskClosable: false,
                });
            });
        });

        const switchMenu = (key: string, op: string) => {
            menuStore.selectedKeys = [key];
            switch (key) {
                case "insert": {
                    insertStore.op = op;
                    menuStore.openKeys = ['page_edit'];
                    break;
                }
                case "merge": {
                    menuStore.openKeys = ['page_edit'];
                    break;
                }
                case "split": {
                    menuStore.openKeys = ['page_edit'];
                    break;
                }
                case "rotate": {
                    menuStore.openKeys = ['page_edit'];
                    break;
                }
                case "delete": {
                    menuStore.openKeys = ['page_edit'];
                    break;
                }
                case "reorder": {
                    menuStore.openKeys = ['page_edit'];
                    break;
                }
                case "crop": {
                    menuStore.openKeys = ['page_edit'];
                    break;
                }
                case "scale": {
                    menuStore.openKeys = ['page_edit'];
                    break;
                }
                case "cut": {
                    cutStore.op = op;
                    menuStore.openKeys = ['page_edit'];
                    break;
                }
                case "header": {
                    menuStore.openKeys = ['page_edit'];
                    break;
                }
                case "page_number": {
                    menuStore.openKeys = ['page_edit'];
                    break;
                }
                case "background": {
                    menuStore.openKeys = ['page_edit'];
                    break;
                }
                case "annot": {
                    annotStore.op = op;
                    menuStore.openKeys = ['page_edit'];
                    break;
                }
                case "watermark": {
                    watermarkStore.op = op;
                    menuStore.openKeys = ['protect'];
                    break;
                }
                case "encrypt": {
                    encryptStore.op = op;
                    menuStore.openKeys = ['protect'];
                    break;
                }
                case "sign": {
                    signStore.op = op;
                    menuStore.openKeys = ['protect'];
                    break;
                }
                case "bookmark": {
                    bookmarkStore.op = op;
                    menuStore.openKeys = ['other'];
                    break;
                }
                case "extract": {
                    extractStore.op = op;
                    menuStore.openKeys = ['other'];
                    break;
                }
                case "compress": {
                    menuStore.openKeys = ['other'];
                    break;
                }
                case "convert": {
                    convertStore.type = op;
                    menuStore.openKeys = ['other'];
                    break;
                }
                case "ocr": {
                    menuStore.openKeys = ['other'];
                    break;
                }
                case "dual": {
                    menuStore.openKeys = ['other'];
                    break;
                }
                case "anki": {
                    menuStore.openKeys = ['other'];
                    break;
                }
            }
        };
        return {
            switchMenu
        };
    }
})
</script>

